import { Cell } from "./Cell";
import { colors } from "./Colors";
import { Figure } from "./figures/Figure";
export class Board {
	cells: Cell[][] = [];
	public initCels() {
		for (let i = 0; i < 8; i++) {
			const row: Cell[] = [];
			for (let j = 0; j < 8; j++) {
				if ((i + j) % 2 !== 0) {
					row.push(new Cell(this, j, i, colors.BLACK, Figure));
				} else {
					row.push(new Cell(this, j, i, colors.BLACK, Figure));
				}
			}
			this.cells.push(row);
		}
	}
}
